import { useState } from "react";
import InputBox from "./components/InputBox";
import List from "./components/List";

function App() {
  const [task, setTask] = useState([{ text: "yyy", status: "pending",}]);

  const addTask = (value) => {
    setTask([...task, value]);
  };

  const deletedTask = (e) =>{
    setTask((prev)=> prev.filter(x => x.text !== e));
  }

  const editTask = (e) =>{
    console.log(e);
    setTask((status) => status.map((stt) => stt.status === e ? {...stt, status:'complete'} : {...stt, status:'pending'}));
  }
  return (
    <>
      <div className="container">
        <header>
          <h1>TODO LIST</h1>
          <div className="alert-message"></div>
          <InputBox addTask={addTask} task={task} />
        </header>

        <table className="table w-full">
            <thead>
                <tr>
                    <th>Task</th>
                    {/* <th>Due Date</th> */}
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody className="todos-list-body">
              <List todo={task} deletedTask={deletedTask} editTask={editTask}/>
            </tbody>
        </table>

        <ul>

        </ul>


      </div>
      {/* 카피라이터 */}
      <div className="author-text">
        <p>
          Made with ❤️ by{" "}
          <a
            href="https://github.com/yujini-lee"
            target="_blank"
            rel="noreferrer"
          >
            <b>yujini-lee</b>
          </a>
        </p>
      </div>
    </>
  );
}

export default App;
