import { useState } from "react";
import InputBox from "./components/InputBox";
import List from "./components/List";

function App() {
  const [task, setTask] = useState([{ text: "yyy", status: "pending" }]);

  const addTask = (value) => {
    setTask([...task, value]);
  };

  const handleModify = () => {};

  return (
    <>
      <div className="container">
        <header>
          <h1>TODO LIST</h1>
          <div className="alert-message"></div>
          <InputBox addTask={addTask} task={task} />
        </header>

        <ul>
          <List handleModify={handleModify} todo={task} />
        </ul>


      </div>
      {/* 카피라이터 */}
      <div className="author-text">
        <p>
          Made with ❤️ by{" "}
          <a
            href="https://github.com/yujini-lee"
            target="_blank"
            rel="noreferrer"
          >
            <b>yujini-lee</b>
          </a>
        </p>
      </div>
    </>
  );
}

export default App;
