import React, { useRef, useState, useEffect } from "react";

function List({ todo, deletedTask, handleUpdate, handleEdit }) {
  const [toggle, setToggle] = useState(false);
  const [text, setText] = useState("");
  const inputRef = useRef();

  useEffect(() => {
    if (inputRef.current && toggle) {
      inputRef.current.focus();
    }
  }, [toggle]);

  const toggleChange = (e) => {
    setText(e.target.value);
  };

  const edit = (idx) => {
    const editedText = todo.find((item) => item.id === idx).content;
    setText(editedText);
    setEditingIndex(idx); // Add this line to set the editing index
    setToggle(true);
  };

  const saveEdit = (idx) => {
    setToggle(false);
    handleEdit(idx, text); // Pass the updated text to handleEdit
  };

  return (
    <>
      {todo.map((x, idx) => (
        <tr className="todo-item" key={idx}>
          {toggle ? (
            <td>
              <input
                type="text"
                value={text}
                onChange={toggleChange}
                ref={inputRef}
                disabled={!toggle}
              />
            </td>
          ) : (
            <td>{x.text}</td>
          )}
          <td>{x.isDone ? "complete" : "pending"}</td>
          <td>
            {toggle ? (
              <button
                className="btn btn-success btn-sm"
                onClick={() => saveEdit(idx)}
              >
                수정
                <i className="bx bx-check bx-xs"></i>
              </button>
            ) : (
              <button
                className="btn btn-warning btn-sm"
                onClick={() => edit(idx)}
              >
                수정하는버튼
                <i className="bx bx-edit-alt bx-bx-xs"></i>
              </button>
            )}
            <button
              className="btn btn-success btn-sm"
              onClick={() => handleUpdate(idx)}
            >
              완성버튼
              <i className="bx bx-check bx-xs"></i>
            </button>
            <button
              className="btn btn-error btn-sm"
              onClick={() => deletedTask(x.content)}
            >
              삭제버튼
              <i className="bx bx-trash bx-xs"></i>
            </button>
          </td>
        </tr>
      ))}
    </>
  );
}

export default List;
