export default function todoReducer(task, action) {
  switch (action.type) {
    case "ADD":
      return task.concat(action.value);
    case 'DELETE' : 
      return task.filter((x) => x.text !== action.todolist);
      default:{
        throw Error('에러');
      }
  }
}
