import React, { useRef, useState, useEffect } from "react";

function List({ todo, deletedTask, handleUpdate, handleEdit }) {
  const [toggle, setToggle] = useState(false);
  const [text, setText] = useState("");
  const inputRef = useRef();
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    if (inputRef.current && toggle) {
      inputRef.current.focus();
    }
  }, [toggle]);

  const toggleChange = (e) => {
    setText(e.target.value);
  };

  const edit = (idx) => {
    setToggle(true);
    setEditingIndex(idx);
    const editedText = todo.find((item) => item.id === idx).content;
    setText(editedText);
  };

  const saveEdit = (idx) => {
    setToggle(false);
    handleEdit(idx, text);
    setEditingIndex(null);
  };

  return (
    <>
      {todo.map((x, idx) => (
        <tr className="todo-item" key={idx}>
          <td>
            {editingIndex === idx ? (
              <input
                type="text"
                value={text}
                onChange={toggleChange}
                ref={inputRef}
              />
            ) : (
              x.text
            )}
          </td>
          <td>{x.isDone ? "complete" : "pending"}</td>
          <td>
            {editingIndex === idx ? (
              <button
                className="btn btn-success btn-sm"
                onClick={() => saveEdit(idx)}
              >
                저장
                <i className="bx bx-check bx-xs"></i>
              </button>
            ) : (
              <button
                className="btn btn-warning btn-sm"
                onClick={() => edit(idx)}
              >
                수정
                <i className="bx bx-edit-alt bx-bx-xs"></i>
              </button>
            )}
            <button
              className="btn btn-success btn-sm"
              onClick={() => handleUpdate(idx)}
            >
              완료
              <i className="bx bx-check bx-xs"></i>
            </button>
            <button
              className="btn btn-error btn-sm"
              onClick={() => deletedTask(x.content)}
            >
              삭제
              <i className="bx bx-trash bx-xs"></i>
            </button>
          </td>
        </tr>
      ))}
    </>
  );
}

export default List;
