import React, { useRef, useState } from "react";

function List({ todo, deletedTask, handleUpdate, handleEdit }) {
  const [toggle, setToggle] = useState(false);
  const [text, setText] = useState('');
  const inputRef = useRef(null);

  const toggleChange = (e) => {
    setText(e.target.value);
  }

  const edit = (idx) => {
    inputRef.current.focus();
    setToggle((prev) => !prev);
    setText(todo.find((item) => item.id === idx).content);
  }

  return (
    <>
      {todo.map((x, idx) => (
        <tr className="todo-item" key={idx}>
          {toggle ? (
            <td>
              <input
                value={text}
                onChange={toggleChange}
                ref={inputRef}
                disabled={!toggle} // Disable input when not in edit mode
              />
            </td>
          ) : (
            <td>{x.content}</td> // Use x.content instead of x.text
          )}
          <td>{x.isDone ? 'complete' : 'pending'}</td>
          <td>
            <button
              className="btn btn-warning btn-sm"
              onClick={() => edit(idx)}
            >
              <i className="bx bx-edit-alt bx-bx-xs"></i>
            </button>
            <button
              className="btn btn-success btn-sm"
              onClick={() => handleUpdate(idx)}
            >
              <i className="bx bx-check bx-xs"></i>
            </button>
            <button
              className="btn btn-error btn-sm"
              onClick={() => deletedTask(x.content)} // Use x.content for deletion
            >
              <i className="bx bx-trash bx-xs"></i>
            </button>
          </td>
        </tr>
      ))}
    </>
  );
}

export default List;
