import { useState } from "react";
import InputBox from "./components/InputBox";
import List from "./components/List";

function App() {
  const [task, setTask] = useState([{id:0, text: "yyy", status: "pending", isDone: false, },{id:1, text: "aaa", status: "pending", isDone: false, }]);

  const addTask = (value) => {
    setTask([...task, value]);
  };

  const deletedTask = (e) =>{
    setTask((prev)=> prev.filter(x => x.text !== e));
  }

  const handleUpdate = (id) =>{
    setTask((todolist) => todolist.map((todo) => todo.id === id ? {...todo, isDone: !todo.isDone} : todo));
    console.log('test');
  }

  const hadleEdit = (id, text) => {
    setTask((todolist) => todolist.map((todo) => todo.id === id ? {...todo, text} : todo));
  }
  return (
    <>
      <div className="container">
        <header>
          <h1>TODO LIST</h1>
          <div className="alert-message"></div>
          <InputBox addTask={addTask} task={task} />
        </header>

        <table className="table w-full">
            <thead>
                <tr>
                    <th>Task</th>
                    {/* <th>Due Date</th> */}
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody className="todos-list-body">
              {
                task !== null ? <List todo={task} deletedTask={deletedTask} handleUpdate={handleUpdate} handleEdit={hadleEdit}/> :
                <tr><td colSpan="5" className="text-center">No task found</td></tr>
              }
            </tbody>
        </table>

        <ul>

        </ul>


      </div>
      {/* 카피라이터 */}
      <div className="author-text">
        <p>
          Made with ❤️ by{" "}
          <a
            href="https://github.com/yujini-lee"
            target="_blank"
            rel="noreferrer"
          >
            <b>yujini-lee</b>
          </a>
        </p>
      </div>
    </>
  );
}

export default App;
